import * as React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import { NavLink } from 'react-router-dom';

const drawerWidth = 240;
const navItems = ['Home', 'About'];

function DrawerAppBar(props) {
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen((prevState) => !prevState);
  };

  const drawer = (
    <Box onClick={handleDrawerToggle} sx={{ textAlign: 'center' }}>
      <List>
        {navItems.map((item) => (
          <ListItem key={item} disablePadding  component={NavLink} to="/">
            <ListItemButton sx={{ textAlign: 'center' }}>
              <ListItemText primary={item} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
      <Box>
            <IconButton color="inherit" component={NavLink} to="/" sx={{ '&:hover': { color: 'red'} }}>
                <FacebookIcon textDecoration="none" color="#fff"/>
            </IconButton>
            <IconButton color="inherit" component={NavLink} to="/" sx={{ '&:hover': { color: 'red'} }}>
                <InstagramIcon textDecoration="none" color="#fff" />
            </IconButton>
            <IconButton color="inherit" component={NavLink} to="/" sx={{ '&:hover': { color: 'red'} }}>
                <TwitterIcon textDecoration="none" color="#fff" />
            </IconButton>
      </Box>
    </Box>
  );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <Box sx={{ display: 'flex'}}>
      <CssBaseline />
      <AppBar component="nav" sx={{ background: '#131313'}}>
        <Toolbar>
          <Box sx={{  flexGrow: 1, display: 'flex', alignItems: "center" }}>
            <img alt="gambian flag" width="30px" height="30px"
              src="data:image/jpeg;base64,"/>
            <Typography
              variant="h6"
              component="div"
              sx={{ margin: "0 1rem", textTransform: 'uppercase', fontSize: { sm: '.6rem', xs: '.6rem', lg: '1.2rem', md: '1rem'} }}
            >
              Clean Water for Rural Gambia
            </Typography>
          </Box>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { sm: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          <Box sx={{ display: { xs: 'none', sm: 'block' } }}>
            {navItems.map((item) => (
              <Button key={item} sx={{ color: '#fff', '&:hover': { background: 'red'} }} component={NavLink} 
              to={`${(item === "Home") ? "/": "/#"+item.toLowerCase()}`}>
                {item}
              </Button>
            ))}
            <Button variant="contained" sx={{ margin: '0 1rem', color: '#fff', '&:hover': { background: 'red'} }}
            component={NavLink} to="/donate">
                Donate
            </Button>
          </Box>
          <Box sx={{ display: { xs: 'none', sm: 'block' } }}>
            <IconButton color="inherit" component={NavLink} to="/" sx={{ '&:hover': { color: 'red'} }}>
                <FacebookIcon textDecoration="none" color="#fff"/>
            </IconButton>
            <IconButton color="inherit" component={NavLink} to="/" sx={{ '&:hover': { color: 'red'} }}>
                <InstagramIcon textDecoration="none" color="#fff" />
            </IconButton>
            <IconButton color="inherit" component={NavLink} to="/" sx={{ '&:hover': { color: 'red'} }}>
                <TwitterIcon textDecoration="none" color="#fff" />
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>
      <Box component="nav">
        <Drawer
          container={container}
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Better open performance on mobile.
          }}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
        >
          {drawer}
        </Drawer>
      </Box>
    </Box>
  );
}

DrawerAppBar.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default DrawerAppBar;